import React, { useState } from 'react';
import CreateArti from './components/Create-article/CreateArti';
import AfficheArti from './components/Create-article/AfficheArti';

// Composant principal de l'application
const App = () => {
  // État pour stocker la liste des articles
  const [articles, setArticles] = useState([
    {
      name: 'Chaise de Bureau Ergonomique',
      category: 'Mobilier de Bureau',
      brand: 'ConfortPlus',
      price: 199.99,
      content: 'Cette chaise de bureau ergonomique offre un confort optimal pour les longues heures de travail. Elle est équipée d\'un dossier ajustable et d\'accoudoirs rembourrés.',
      stock: 25,
      online: true,
      pictures: ['chaise1.jpg', 'chaise2.jpg', 'chaise3.jpg', 'chaise4.jpg']
    },
    {
      name: 'Lampe de Bureau LED',
      category: 'Éclairage',
      brand: 'LumièrePro',
      price: 49.99,
      content: 'Lampe de bureau LED avec intensité réglable et bras flexible pour un éclairage optimal de votre espace de travail.',
      stock: 50,
      online: true,
      pictures: ['lampe1.jpg', 'lampe2.jpg', 'lampe3.jpg', 'lampe4.jpg']
    }
  ]);


  // Fonction pour ajouter un nouvel article à la liste
  const addArticle = (article) => {
    setArticles([...articles, article]);
  };

  return (
    <div>
      <CreateArti addArticle={addArticle} />
      <AfficheArti articles={articles} />
    </div>
  );
};

export default App;
