import axios from "axios";
import React, { useState } from "react";

const Add = () => {
  const [formData, setFormData] = useState({
    name: "",
    category: "",
    brand: "",
    price: "",
    content: "",
    stock: "",
    online: "",
    pictures: {
      img: "",
      img1: "",
      img2: "",
      img3: "",
      img4: "",
    },
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // const handlePictureChange = (e) => {
  //   const { name, files } = e.target;
  //   setFormData({
  //     ...formData,
  //     pictures: {
  //       ...formData.pictures,
  //       [name]: files[0], // Assuming single file upload
  //     },
  //   });
  // };

  const handleSubmit = async () => {

    try {
      const { data } = await axios.post(
        "http://localhost:8000/api/article/add",
        {
          name: "",
          category: "",
          brand: "",
          price: "",
          content: "",
          stock: "",
          online: "",
          picture: [
            {
              img: "",
              required: true,
              img1: "",
              img2: "",
              img3: "",
              img4: "",
            },
          ],
        },
        {
          headers: {
           'Content-Type': 'application/x-www-form-urlencoded'
          },
        }
      );

         console.log(data);
    } catch (err) {
      console.log(err.message);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="name"
        placeholder="Name"
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="category"
        placeholder="Category"
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="brand"
        placeholder="Brand"
        onChange={handleChange}
        required
      />
      <input
        type="number"
        name="price"
        placeholder="Price"
        onChange={handleChange}
        required
      />
      <textarea
        name="content"
        placeholder="Content"
        onChange={handleChange}
        required
      />
      <input
        type="number"
        name="stock"
        placeholder="Stock"
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="online"
        placeholder="Online"
        onChange={handleChange}
        required
      />

      <h4>Upload Images:</h4>
      <input type="text" name="img" onChange={handleChange} required />
      <input type="text" name="img1" onChange={handleChange} />
      <input type="text" name="img2" onChange={handleChange} />
      <input type="text" name="img3" onChange={handleChange} />
      <input type="text" name="img4" onChange={handleChange} />

      <button type="submit">Submit</button>
    </form>
  );
};

export default Add;
