import React, { useEffect, useState } from "react";
import Plus from "../IMAGES/plus.png";
import Get from "../FUNCTIONS/Get";
import axios from "axios";
import Add from "./Add";
import { Link } from "react-router-dom";

const Article = () => {
  const [users, setUsers] = useState([]);

  const handleDelete = async (id) => {
    try {
      const { data, status } = await axios.delete(
        `http://localhost:8000/api/article/delete/${id}`
      );
      if (status === 200) console.log("item deleted");
      console.log(data, status);
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    const Fetch = async () => {
      try {
        const { data, status } = await axios.get(
          "http://localhost:8000/api/article/get"
        );

        console.log(data);
        console.log(status);

        if (status === 200) setUsers(data);
        console.log("bon");
      } catch (err) {
        // console.log(err.message);
      }
    };
    Fetch();
  }, []);
  //   console.log(users);
  return (
    <div>
      <div>
        {users.map((data,index) => (
          <div>
            <p>name:{data?.name} </p>
            <p>username:{data?.brand} </p>
            <p>email:{data?.price} </p>
            <p>name:{} </p>
            <p>name:{} </p>
            <p>name:{} </p>
            <button onClick={() =>handleDelete(index)}>delete</button>
          </div>
        ))}
      </div>
      <Link to="/add">
        <img src={Plus} width={100} alt="corbeille" />
      </Link>
    </div>
  );
};

export default Article;
