        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="name"
            placeholder="Name"
            onChange={handleChange}
            required
          />
          <input
            type="text"
            name="category"
            placeholder="Category"
            onChange={handleChange}
            required
          />
          <input
            type="text"
            name="brand"
            placeholder="Brand"
            onChange={handleChange}
            required
          />
          <input
            type="number"
            name="price"
            placeholder="Price"
            onChange={handleChange}
            required
          />
          <textarea
            name="content"
            placeholder="Content"
            onChange={handleChange}
            required
          />
          <input
            type="number"
            name="stock"
            placeholder="Stock"
            onChange={handleChange}
            required
          />
          <input
            type="text"
            name="online"
            placeholder="Online"
            onChange={handleChange}
            required
          />

          <h4>Upload Images:</h4>
          <input type="text" name="img" onChange={handleChange} required />
          <input type="text" name="img1" onChange={handleChange} />
          <input type="text" name="img2" onChange={handleChange} />
          <input type="text" name="img3" onChange={handleChange} />
          <input type="text" name="img4" onChange={handleChange} />

          <button type="submit">Submit</button>
        </form>
      );
}

export default Page
