import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const Page = () => {
  const {id}= useParams();


  const [values, setValues] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    category: "",
    brand: "",
    price: 0,
    content: "",
    stock: 0,
    online: false,
    pictures: {
      img: "",
      img1: "",
      img2: "",
      img3: "",
      img4: "",
    },
  });

  useEffect(() => {
    const GetById = async () => {
      try {
        const { data, status } = await axios.get(
          `http://localhost:8000/api/article/get/${id}`
        );

        if (status === 200) setValues(data); 

        console.log("good");
      } catch (err) {
        console.log(err);
      }
    };
    GetById();
  }, []);

  console.log(values);


  const Put = async () => {
    try {
      const { data, status } = await axios.put(
        `http://localhost:8000/api/article/update/${id}`,
        formData
      );

      if (status === 200) console.log("bon");
    } catch (err) {
      console.log(err);
    }
  };

  const handleChange = (index,event) => {
    const { name, value } = event.target;
    const updatedArticles = [...articles];
    updatedArticles[index][name] = value;
    setFormData(updatedArticles);
  };

  return (
    <form onSubmit={''}>
      <input
        type="text"
        name="name"
        value={values?.name}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="category"
        value={values?.category}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="brand"
        value={values?.brand}
        onChange={handleChange}
        required
      />
      <input
        type="number"
        name="price"
        value={values?.price}
        onChange={handleChange}
        required
      />
      <textarea
        name="content"
        value={values?.content}
        onChange={handleChange}
        required
      />
      <input
        type="number"
        name="stock"
        value={formData?.stock}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="online"
        value={values?.online}
        onChange={handleChange}
        required
      />

      <h4>Upload Images:</h4>
      <input
        type="text"
        name="img"
        value={values?.pictures}
        onChange={handleChange}
        required
      />
      <input type="text" name="img1" onChange={handleChange} />
      <input type="text" name="img2" onChange={handleChange} />
      <input type="text" name="img3" onChange={handleChange} />
      <input type="text" name="img4" onChange={handleChange} />

      <button type="submit">Submit</button>
    </form>
  );
};

export default Page;
