import React, { useState } from 'react';

const Create = ({ addArticle }) => {
  const [article, setArticle] = useState({
    name: ' Chaise Bureau ',
    category: 'Mobilier de Bureau',
    brand: 'ConfortPlus',
    price: 199.99,
    content: 'Cette chaise est très confortable.',
    stock: 25,
    online: true,
    pictures: ['chaise1.jpg', 'chaise2.jpg', 'chaise3.jpg', 'chaise4.jpg']
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setArticle({ ...article, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addArticle(article);
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="name" value={article.name} onChange={handleChange} placeholder="Nom" />
      <input type="text" name="category" value={article.category} onChange={handleChange} placeholder="Catégorie" />
      <input type="text" name="brand" value={article.brand} onChange={handleChange} placeholder="Marque" />
      <input type="number" name="price" value={article.price} onChange={handleChange} placeholder="Prix" />
      <textarea name="content" value={article.content} onChange={handleChange} placeholder="Contenu"></textarea>
      <input type="number" name="stock" value={article.stock} onChange={handleChange} placeholder="Stock" />
      <input type="checkbox" name="online" checked={article.online} onChange={() => setArticle({ ...article, online: !article.online })} />
      <input type="text" name="pictures" value={article.pictures.join(', ')} onChange={handleChange} placeholder="Images (séparées par des virgules)" />
      <button type="submit">Ajouter l'article</button>
    </form>
  );
};

export default Create;
